INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('0', 'Venom: The Last Dance', 'Venom: The Last Dance', '109', '37POW7X4SIbdfbqWznG3ceU5DTQ.jpg', 'In Venom: The Last Dance, Tom Hardy torna a interpretare Venom, uno dei più celebri e complessi personaggi della Marvel. Nel film conclusivo della trilogia, Eddie e Venom sono in fuga. Mentre il cerchio si stringe attorno a loro, i due protagonisti sono costretti a prendere una decisione sconvolgente che farà calare il sipario sul loro “ultimo ballo”.', 'Rilasciato', '2024-10-22', '120000000', '438325476', '558216', 'en');
INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('1', 'Il robot selvaggio', 'The Wild Robot', '102', 'j48mq3yF4Ghi8CclXqqZMWVByk1.jpg', 'L''epica avventura segue il viaggio di un robot - l''unità ROZZUM 7134, abbreviato ""Roz"" - che dopo un naufragio si ritrova su un''isola disabitata dove dovrà imparare ad adattarsi all’ostile ambiente circostante, costruendo gradualmente relazioni con gli altri animali dell’isola e adottando un''ochetta orfana.', 'Rilasciato', '2024-09-12', '78000000', '308583746', '1370345', 'en');
INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('2', 'Terrifier 3', 'Terrifier 3', '125', 'h24qwvXSXIAVXsbftPhNtYEkoLg.jpg', 'Quando gli abitanti di Miles County si coricano nelle loro case alla Vigilia di Natale, non hanno idea che a scendere dal camino stavolta sarà Art il Clown. Il folle killer ha preso il posto di Babbo Natale e le sue povere vittime sono ignare che, invece dei giocattoli, saranno loro a riempire il sacco dei regali.', 'Rilasciato', '2024-10-09', '2000000', '86813848', '727761', 'en');
INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('3', 'Il gladiatore II', 'Gladiator II', '148', 'v1ffLD5yQqpJW1VXYy1n6sp5wgX.jpg', 'Anni dopo aver assistito alla tragica morte del venerato eroe Massimo per mano del suo perfido zio, Lucio (Paul Mescal) si trova costretto a combattere nel Colosseo dopo che la sua patria viene conquistata da parte di due tirannici imperatori, che ora governano Roma.  Con il cuoreardente di rabbia e il destino dell''Impero appeso a un filo, Lucio deve affrontare pericoli e nemici, riscoprendo nel suo passato la forza e l''onore necessari per riportare la gloria di Roma al suo popolo. Preparatevi per un viaggio epico di coraggio e vendetta nella sanguinosa arena del Colosseo.', 'Rilasciato', '2024-11-13', '310000000', '98901925', '1069584', 'en');
INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('4', 'Smile 2', 'Smile 2', '127', 'pGWPldQcP9cw0Wo3EVLNSLTBVX0.jpg', 'Sul punto di iniziare un nuovo tour mondiale, la star internazionale del pop Skye Riley (Naomi Scott) inizia a vivere esperienze sempre più terrificanti e incomprensibili. Schiacciata dalle crescenti angosce e dalle pressioni della notorietà, Skye è costretta a confrontarsi con il suo passato oscuro per riprendere in mano la sua vita prima che vada in pezzi.', 'Rilasciato', '2024-10-16', '28000000', '132416306', '1100788', 'en');
