INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('6', 'Wicked', 'Wicked', '161', '3al2ouBCaZN5UcCNr8HKSiNji28.jpg', 'La storia ruota attorno all’amicizia tra le due streghe. Elphaba, soprannominata ingiustamente Malvagia Strega dell’Ovest, è in realtà una donna rivoluzionaria e anticonformista con la pelle verde che sfida lo strapotere del Mago di Oz, imbroglione dalle mire imperialiste. Glinda è invece la Strega Buona del Nord, amica di Elphaba dai tempi dell’Università di Shiz.', 'Rilasciato', '2024-11-20', '145000000', NULL, '968080', 'en');
INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('7', 'The Substance', 'The Substance', '141', 'fE4dIJMT2A6KsfRhgEEhMYcruS.jpg', 'Hai mai sognato una versione migliore di te? Tu. Semplicemente, migliore, in ogni senso. Davvero. Devi provare questo nuovo prodotto. Si chiama “la sostanza”. HA CAMBIATO LA MIA VITA. Genera un altro te. Un te nuovo, più giovane, più bello, più perfetto. E c’è solo una regola: vi dovete dividere il tempo. Una settimana sta a te. Quella dopo sta al nuovo te. Sette giorni a testa. Un equilibrio perfetto. Facile, no? Se rispetti l’equilibrio… cosa può andare storto?', 'Rilasciato', '2024-09-07', '17500000', '46818041', NULL, 'en');
INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('8', 'Transformers One', 'Transformers One', '104', '41caGYMJKZMjlwFXwsIxL1UtUN.jpg', 'La storia inedita delle origini di Optimus Prime e Megatron, meglio conosciuti come nemici giurati, ma un tempo amici legati come fratelli che hanno cambiato per sempre il destino di Cybertron.', 'Rilasciato', '2024-09-11', '75000000', '124530200', NULL, 'en');
INSERT INTO movies (id, title, original_title, runtime, poster, overview, status, release_date, budget, revenue, collection_id, original_language) VALUES ('9', 'CIA - Un uomo nel mirino', 'Classified', '105', 'dAfwsVCHYCEJgKMcL4NEsnsDzCM.jpg', 'Un sicario professionista e di lungo corso al soldo della CIA lavora prendendo ordini esclusivamente attraverso la sezione degli annunci personali di vari quotidiani. Improvvisamente, però, grazie a un''osservazione della figlia il killer scopre che la divisione per la quale credeva di operare è stata chiusa diversi anni prima.', 'Rilasciato', '2024-09-19', NULL, NULL, NULL, 'en');
